mapper.select:
select t.id as "id", tt.pid as "pid", t.title as "title", t.form_code as "form_code", t.service_type as "service_type", t.service_type_name as "service_type_name", t.service_name as "service_name", t.county as "county", o.org_name as "county_name", t.branch_office as "branch_office", o2.org_name as "branch_office_name", t.channel as "channel", o3.org_name as "channel_name", t.channel_phone as "channel_phone", t.user_phone as "user_phone", t.creater as "creater", t.created as "created", t.description as "description", t.remark as "remark", t.status as "status", t.ext1 as "ext1", t.ext2 as "ext2", t.row_version as "row_version", jhp.STATE_ from t_work_progress_feedback t left join sys_org o on t.county = o.org_code left join sys_org o2 on t.branch_office = o2.org_code left join sys_org o3 on t.channel = o3.org_code left join t_task tt on t.id = tt.form_id left join jbpm4_hist_procinst jhp on jhp.ID_ = tt.PID where 1=1
and t.id = #{id}
and tt.pid = #{pid}
and t.service_type = #{service_type}
and t.county = #{county}
and t.branch_office = #{branch_office}
and t.channel = #{channel}
and t.channel_phone like concat('%',#{channel_phone},'%')
and t.user_phone like concat('%',#{user_phone},'%')
${orderStr}
order by t.CREATED desc
